package com.jugheadzhou.base.producer.controller;import com.jugheadzhou.base.producer.service.IUserServer;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @description: 模拟用户系统 * @author: aishu * @create: 2019-10-17 18:05 */@Slf4j@RestController@RequestMapping("hello")public class UserController {    @Autowired    private IUserServer IUserServer;    /**     * 模拟登陆业务     * @param username     * @param password     * @return     */    @GetMapping("login")    public String login(String username,String password){        log.info("开始登录...");        return this.IUserServer.login(username,password);    }    /**     * 模拟删除用户积分     * @param username     * @return     */    @GetMapping("score/delete")    public String deleteScore(String username){        log.info("删除开始...");        return this.IUserServer.deleteScore(username);    }}